# A와 B
def grid(s, t):
    result = True
    while s != t:
        if len(t) == 0: 
            result = False
            break

        if t[-1] == 'B': 
            t = t[:-1]
            t = t[::-1]
        elif t[-1] == 'A': 
            t = t[:-1]
    
    if result: print(1)
    else: print(0)

if __name__ == "__main__":
    S = input()
    T = input()
    grid(S, T)



